import { ArchetypedExtension, ExtensionConfig } from 'archetyped/lib';
import fs from 'fs';

/**
 * Sample trusted extension
 */
export default class TrustedExtension extends ArchetypedExtension {
  constructor(config: ExtensionConfig, imports: any) {
    super(config, imports);
    this.register('trustedExtension', this);
  }

  trustedMethod() {
    fs.lstat(__dirname, () => {
      console.log('I can read your files, but you trust me anyways ❤️');
    });
  }
}
